mapper.select:
SELECT COUNT(userID) FROM tb_user


        username LIKE '%${username}%'


        AND status = #{status}

SELECT userID,username,nickName,status,role,pictureUrl FROM tb_user



        username LIKE '%${username}%'


        AND
        status = #{status}

    AND userID Not In (Select * from (Select userID From tb_user



        username LIKE '%${username}%'


        AND
        status = #{status}


Limit #{startRow})as t) Limit #{rowsOnePage}

SELECT tb_user.*,name as agencyName FROM tb_user
LEFT JOIN tb_agency ON agencyID = tb_agency.id

    userID = #{userID}

mapper.update:
UPDATE tb_user
username = #{userDetail.username}, nickName = #{userDetail.nickName}, status = #{userDetail.status}, role = #{userDetail.role}, email = #{userDetail.email}, phoneNumber = #{userDetail.phoneNumber}, sex = #{userDetail.sex}, birthday = #{userDetail.birthday}, agencyID = #{userDetail.agencyID}, pictureUrl = #{userDetail.pictureUrl}, lastUpdateTime = now()
,pwd = #{userDetail.pwd}
userID = #{userDetail.userID}
